function cMarker(map,id,lat,lon,nombre,numero,hora,fecha){
  this.map=map;
  this.lat=lat;
  this.lon=lon;
  this.marker=null;
  this.id=id; 
  this.nombre = nombre;
  this.numero = numero;
  this.hora = hora;
  this.fecha = fecha;
  //console.log("Mapa: "+this.map+" Longitud: "+this.lon);
  ///////////////////////////////////////////
  this.dibujar=function(){  
  this.marker=new google.maps.Marker({
    position:new google.maps.LatLng(this.lat,this.lon),
    map:this.map,
    title:this.nombre+" "+this.numero+" Hace:"+this.hora+" "+this.fecha,
    icon: 'img/taxi.png'
    });
  this.marker.setMap(this.map);
  }//////////////////////////////////////////
  this.remover=function(){
  if(this.marker!=null)
    this.marker.setMap(null);
  }//////////////////////////////////////////
  this.desplazarY=function(){
    this.lat+=0.01;

  }//////////////////////////////////////////
  this.desplazarX=function(){
    this.lon+=0.01;

  }////////////////////////////////////////// 
  this.getId=function(){
    return this.id;
  }////////////////////////////////////////// 
  this.getNombre=function(){
    return this.nombre;
  }
  this.getNumero=function(){
    return this.numero;
  }
  this.getHora=function(){
    return this.hora;
  }
  this.update=function(lat,lon,hora){
    this.lat=lat;
    this.lon=lon; 
    this.hora=hora;
  console.log("Latitud: "+this.lat+" Longitud: "+this.lon);
  }//////////////////////////////////////////           
}
